-- Create ADF factory as a user in the SQL pool
CREATE USER [AirBnB-Project-DF] FROM EXTERNAL PROVIDER;

-- Give full permissions (db_owner)
EXEC sp_addrolemember 'db_owner', 'AirBnB-Project-DF';

-- Verify
SELECT name, type_desc FROM sys.database_principals WHERE name = 'AirBnB-Project-DF';
